import React, { useState } from "react";
import Input from "./Input";
import { handleClick } from "./Alert";
import { useNavigate } from 'react-router-dom';
import { getInitialData } from "../utils";

const Content = ({ setNotes }) => {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
const [data, setData] = useState(getInitialData())

  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!title || !body) {
      handleClick({
        title: "Error",
        text: "Titles and Notes are Required!",
        icon: "error",
      });
      return;
    }

 // Find the maximum ID in the current data
 const maxId = Math.max(...data.map((note) => note.id), 0);

 // Increment the maximum ID by 1 to generate a new unique ID
    const newId = maxId + 1;
    const date = new Date().toISOString();
    const note = { id: newId, title, body, archived: false, createdAt: date };

    
    setNotes((prevNotes) => [...prevNotes, note]);
    setTitle("");
    setBody("");
  };
  

  return (
    <div className="note-input">
      <h1>New Notesâœï¸</h1>
      <form onSubmit={handleSubmit}>
        <p className="note-input__title__char-limit">
          Characters left : {50 - title.length}
        </p>
        <Input
          value={title}
          onChange={setTitle}
          name="title"
          type="text"
          placeholder="âœ’ï¸Title"
        />
        <Input
          value={body}
          onChange={setBody}
          name="body"
          type="textarea"
          placeholder="âœï¸Write the notes here"
        />
        <button
          type="submit"
          className="create-btn"
          onClick={() =>
            handleClick({
              title: "Success Create Note",
              text: `Note "${title}" has been created`,
              icon: "success",
            },
            setTimeout(() => {
              navigate('/home');
            }, 100)
            )}
        >
          ğŸ“¤Submit
        </button>
      </form>
    </div>
  );
};

export default Content;
