import React from "react";
import Action from "./Action";
import ShowItems from "./ShowItems";
import { handleClickConfirm } from "./Alert";

const Items = ({ id, title, body, createdAt, archived, action }) => {
  const onDeleteNote = (item) => {
    handleClickConfirm(
      item,
      action,
      "delete",
      "Are you sure?",
      "You won't be able to revert this!",
      "warning",
      "Yes, delete it!"
    );
  };

  const onArchiveNote = (item) => {
    !archived
      ? handleClickConfirm(
          item,
          action,
          "archive",
          "Are you sure?",
          "Do you want to 'Archive' this note?",
          "info",
          "Yes, archive it!"
        )
      : handleClickConfirm(
          item,
          action,
          "unarchive",
          "Are you sure?",
          "Do you want to 'unarchive' this note?",
          "info",
          "Yes, Unarchive it!"
        );
  };

  return (
    <div className="note-item">
      <ShowItems
        title={title}
        body={body}
        createdAt={createdAt}
        archived={archived}
        action={action}
      />

      <Action
        onDeleteNote={onDeleteNote}
        onArchiveNote={onArchiveNote}
        id={id}
        archived={archived}
      />
    </div>
  );
};

export default Items;
