import { useEffect, useState } from "react";
import Body from "./components/Body";
import Home from "./pages/home";
import Header from "./components/Header";
import Footer from "./components/Footer";
import { getInitialData } from "./utils";
import { Router, Route, Routes, useLocation, Navigate } from "react-router-dom"; // Perhatikan penambahan 'dom' di sini

function App() {
  const [query, setQuery] = useState("");
  const [searchNotes, setSearchNotes] = useState([]);
  const [notes, setNotes] = useState(getInitialData());

  const notesAll = (searchNotes || notes).filter((note) => !note.archived);
  const notesArchive = (searchNotes || notes).filter((note) => note.archived);

  useEffect(() => {
    setSearchNotes(
      notes.filter((note) =>
        note.title.toLowerCase().includes(query.toLowerCase())
      )
    );
  }, [query, notes]);

  

  return (
      <div className="note-app">
        <Header search={query} setQuery={setQuery} />
        
        <Routes>
          <Route path="/home" element={<Home />} />
        </Routes>
        
        {/* <Body
          notesAll={notesAll}
          notesArchive={notesArchive}
          setNotes={setNotes}
        /> */}
        <Footer />
      </div>
  );
}

export default App;
