import React, { useState } from "react";
import Input from "./Input";
import { handleClick } from "./Alert";
import { useHistory } from 'react-router-dom';

const Content = ({ setNotes }) => {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!title || !body) {
      handleClick({
        title: "Error",
        text: "Titles and Notes are Required!",
        icon: "error",
      });
      return;
    }

    const id = new Date().toISOString();
    const note = { id, title, body, archived: false, createdAt: id };

    setNotes((prevNotes) => [...prevNotes, note]);
    setTitle("");
    setBody("");
  };

  return (
    <div className="note-input">
      <h1>New Notes</h1>
      <form onSubmit={handleSubmit}>
        <p className="note-input__title__char-limit">
          Characters left: {50 - title.length}
        </p>
        <Input
          value={title}
          onChange={setTitle}
          name="title"
          type="text"
          placeholder="Title"
        />
        <Input
          value={body}
          onChange={setBody}
          name="body"
          type="textarea"
          placeholder="Write the notes here"
        />
        <button
          type="submit"
          className="create-btn"
          onClick={() =>
            handleClick({
              title: "Success Create Note",
              text: `Note "${title}" has been created`,
              icon: "success",
            }).the(useHistory.push('/home'))
          }
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default Content;
